unblockall()
{
FILE=/etc/blockdragon/sites.cake
if [ -f "$FILE" ]; then
clear
else
sudo touch /etc/blockdragon/sites.cake
fi
FILE2=/etc/blockdragon/dragon.block
if [ -f "$FILE2" ]; then
clear
else
sudo touch /etc/blockdragon/dragon.block
fi
if [ -d "$FILE4" ]; then
clear
else
sudo mkdir /etc/blockdragon/blocks
chmod 777 /etc/blockdragon/blocks
fi
clear
tput setaf 7 ; tput setab 4 ; tput bold ; printf '%30s%s%-10s\n' "UnBLOCK" ; tput sgr0 ; echo ""
tput setaf 2 ; tput bold ; printf '%s' "|1|"; tput setaf 6 ; printf '%s' " UnblockALL" ; echo ""
tput setaf 2 ; tput bold ; printf '%s' "|2|"; tput setaf 6 ; printf '%s' " Back to menu" ; echo ""
echo ""
tput setaf 7 ; tput setab 4 ; tput bold ; printf '%30s%s%-10s\n' "Select a Option" ; tput sgr0 ; echo ""
echo -n "> "
read option
if [ "$option" = "1" ]; then 

tput setaf 7 ; tput setab 4 ; tput bold ; printf '%30s%s%-10s\n' "This gonna reset the IPTABLES !" ; tput sgr0 ; echo ""
tput setaf 7 ; tput setab 4 ; tput bold ; printf '%30s%s%-10s\n' "Continue?" ; tput sgr0 ; echo ""
tput setaf 7 ; tput setab 4 ; tput bold ; printf '%30s%s%-10s\n' "Y/N" ; tput sgr0 ; echo ""
echo -n "> "
read option2
if [ "$option2" = "Y" ]; then 
clear
sudo iptables -F 
tput setaf 7 ; tput setab 4 ; tput bold ; printf '%30s%s%-10s\n' "DONE" ; tput sgr0 ; echo ""
rm -f /etc/blockdragon/sites.cake
rm -f /etc/blockdragon/dragon.block
rm -f -r /etc/blockdragon/blocks/
sleep 3
unblockall
elif [ "$option2" = "N" ]; then 
unblockall
else
clear
tput setaf 7 ; tput setab 4 ; tput bold ; printf '%30s%s%-10s\n' "ONLY Y our N" ; tput sgr0 ; echo ""
sleep 3
unblockall
fi
elif [ "$option" = "2" ]; then 
menu
else
clear
tput setaf 7 ; tput setab 4 ; tput bold ; printf '%30s%s%-10s\n' "ONLY 1 our 2" ; tput sgr0 ; echo ""
sleep 3
unblockall
fi
}

reblockall()
{
chmod +x /etc/blockdragon/dragon.block
/etc/blockdragon/dragon.block
clear
tput setaf 2 ; tput bold ; printf '%s' "|*|"; tput setaf 6 ; printf '%s' " REBLOCKED LIST" ; tput setaf 2 ; tput bold ; printf '%s' "|*|"; echo ""
sleep 3
menu
}

